import './App.css';
import "./styles.css";
import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAddressBook, faCheckSquare, faCoffee, faEnvelope, faHome, faMicrophoneAlt, faPhone, faPhoneSlash, faPhoneSquare, faUser } from '@fortawesome/fontawesome-free-solid'

const App = () => {
  const [contacts, setContacts] = React.useState([]);
  React.useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then(response => response.json())
      .then(data => {
        setContacts(data);
      });
  }, []);

  return (
    <div className = "container">
      <h1>Contact List</h1>
    {contacts.map(contact => (
        <ContactCard
            avatar={contact.picture}
            name={contact.name}
            email={contact.email}
            phone={contact.phone}
            address={contact.address.suite+','+contact.address.street+","+contact.address.city+","+contact.address.zipcode}
        />
      ))}
      </div>
  );
}

const ContactCard = props => {
  const [showNum, setShowNum] = React.useState(false);
  return (
    <div className="contact-card">
      <FontAwesomeIcon icon={faAddressBook} style={{fontSize:24}}/>
      {/* <img src={props.avatar} alt="profile" /> */}
      <div className="user-details">
        <p><FontAwesomeIcon icon={faUser} /> Name: {props.name}</p>
        <p><FontAwesomeIcon icon={faEnvelope} /> Email: {props.email}</p>
        <button className = "info" onClick={() => setShowNum(!showNum)}>
          More Info
        </button >
        {showNum &&
        <div className = "more-info">
        <p><FontAwesomeIcon icon={faPhoneSquare} /> Phone: {props.phone}</p>
        <p><FontAwesomeIcon icon={faHome} /> Address: {props.address}</p>
        </div>}
      </div>
    </div>

  );
};
export default App;
